# 3. Initialize Session State
# This replaces the "global" variables used in Tkinter
if 'current_index' not in st.session_state:
    st.session_state.current_index = random.randint(0, len(df) - 1)
if 'show_ans' not in st.session_state:
    st.session_state.show_ans = False

# 4. Functions for Logic
def next_question():
    st.session_state.current_index = random.randint(0, len(df) - 1)
    st.session_state.show_ans = False

def toggle_answer():
    st.session_state.show_ans = True

# 5. Display UI
row = df.iloc[st.session_state.current_index]

with st.container(border=True):
    st.subheader(row['rule'])
    
    st.markdown(f"<p style='font-size: 24px;'>‚ùì {row['question']}</p>", unsafe_allow_html=True)
    
    if st.session_state.show_ans:
        # Custom CSS for a large green "Success" box
        st.markdown(
            f"""
            <div style="
                background-color: #d4edda; 
                color: #155724; 
                padding: 20px; 
                border-radius: 10px; 
                border: 1px solid #c3e6cb;
                margin-top: 20px;">
                #<strong style="font-size: 20px;">‚úÖ</strong><br>
                <span style="font-size: 24px;">{row['answer']}</span>
            </div>
            """, 
            unsafe_allow_html=True
        )

